!function(){const e=5e3;let t=!1;const s=new XMLHttpRequest;s.open("GET","http://www.higherlowergame.com/questions/get/general",!1),s.setRequestHeader("User-Agent","Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/99.0.4844.82 Safari/537.36"),s.send();const n=JSON.parse(s.responseText),l=new Map;for(let e=0;e<n.length;e++)l.set(n[e].keyword.toUpperCase(),n[e].searchVolume);setInterval(()=>{t||0!==document.getElementsByClassName("term-volume__volume").length&&(t=!0,setTimeout(()=>{if(null!=n){let s=setInterval(()=>{if(0!==document.getElementsByClassName("term-volume__volume").length){let e=document.getElementsByClassName("term-keyword__keyword")[0].innerText,t=l.get(e.substring(1,e.length-1).toUpperCase()),s=document.getElementsByClassName("term-keyword__keyword")[1].innerText,n=l.get(s.substring(1,s.length-1).toUpperCase());n>t?document.getElementsByClassName("term-actions__button--higher")[0].click():document.getElementsByClassName("term-actions__button--lower")[0].click()}else clearInterval(s),t=!1},e)}else t=!1},2e3))},500)}();